import React, { useState, useEffect } from "react";
import NavbarComp from "../../../components/navbar/NavbarComp";
import stylechat from "./chat.module.css";
import { Row, Col } from "react-bootstrap";
import { io } from "socket.io-client";

let socket;
function UserChat() {
  const [Message, setMessage] = useState("");
  const [pesan, setpesan] = useState([]);
  const [chat, setchat] = useState([]);

  useEffect(() => {
    socket = io("http://localhost:5005");

    socket.on("message", (message) => {
      setpesan([...pesan, message]);
    });
    return () => {
      socket.disconnect();
    };
  }, []);

  const handleMessage = (e) => {
    setMessage(e.target.name);
  };

  const handleSend = () => {
    socket.emit("user", Message);
    setpesan([...pesan, Message]);
  };
  console.log(pesan);
  return (
    <>
      <NavbarComp />

      <div className={stylechat.container}>
        <Row>
          <Col md={2}></Col>
          <Col md={9}>
            <div className={stylechat.containermessage}>
              <br />
              <br />
              {pesan?.map((item, idx) => (
                <p key={idx} className={stylechat.chat}>
                  {item}
                </p>
              ))}
            </div>

            <input
              type="text"
              name="message"
              className={stylechat.inputmessage}
              onChange={handleMessage}
            />

            <button onClick={handleSend} className={stylechat.btnchat}>
              Send
            </button>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default UserChat;
