import React, { useState, useEffect, useContext } from "react";
import { Container, Row, Col, Table, Card } from "react-bootstrap";
import stylepay from "./stylepay.module.css";
import NavbarComp from "../../../components/navbar/NavbarComp";
import logic from "../../../assets/icons/Icon2.png";
import pay from "../../../assets/icons/receipt.png";
import { Link, useHistory } from "react-router-dom";
import swal from "sweetalert";
import { UserContext } from "../../../context/Contextusr";

function OrderPage() {
  let history = useHistory();
  const rp = require("rupiah-format");
  const orders = JSON.parse(localStorage.getItem("ordertrip"));
  const [state, dispatch] = useContext(UserContext);
  const [order, setOrder] = useState([]);
  const [Userorder, setUserorder] = useState([]);
  const [form, setForm] = useState({
    payment: "",
  });

  const [previmage, setprevimage] = useState(null);
  const user = JSON.parse(localStorage.getItem("userlogin"));
  const data = orders?.filter((x) => x?.userorder === user?.fullname);
  useEffect(() => {
    setOrder(data);
  }, []);

  const handleChange = (e, idx) => {
    setForm({
      ...form,
      [e.target.name]:
        e.target.type === "file" ? e.target.files : e.target.value,
    });
    if (e.target.name === "payment") {
      let url = URL.createObjectURL(e.target.files[0]);
      setprevimage(url);
    }
  };

  const handlePayment = (id) => {
    let newData = {
      id: order[id]?.id,
      userorder: order[id]?.userorder,
      title: order[id]?.title,
      day: order[id]?.day,
      night: order[id]?.night,
      country: order[id]?.country,
      eat: order[id]?.eat,
      datebooking: new Date().getTime().toString(),
      date: order[id]?.date,
      subtotal: order[id].subtotal,
      detail: order[id]?.price,
      paymentprof: previmage,
      acomodation: order[id]?.acomodation,
      transpotation: order[id]?.transpotation,
      qty: order[id]?.qty,
      status: "waiting approve",
    };

    const datafilter = order?.filter((itm, idx) => idx !== id);
    setUserorder([...datafilter, newData]);

    dispatch({
      type: "PAYMENT",
      payload: [...datafilter, newData],
    });

    history.push("/client/profile");
    swal("Payment Success");
  };

  return (
    <>
      <NavbarComp />

      <div className={stylepay.container - order}>
        <Row className={stylepay.content}>
          {order?.map((item, idx) => (
            <Col md={12} key={idx}>
              <Card className={stylepay.cardorder}>
                <Row>
                  <Col md={6}>
                    <img src={logic} alt="logodfs" width="140" />
                  </Col>
                  <Col md={6}>
                    <h3 className={stylepay.logoboking}>Booking</h3>
                  </Col>
                </Row>
                <Row>
                  <Col md={3} className="mt-4">
                    <p className={stylepay.title}>{item?.title}</p>
                    <p className={stylepay.country}>{item?.country}</p>
                    <p className={stylepay.stspayment}>{item?.status}</p>
                  </Col>
                  <Col md={3} className="mt-4">
                    <div className={stylepay.contentor}>
                      <p className={stylepay.ttl}>Date Trip</p>
                      <p className={stylepay.resultitem}>{item?.date}</p>
                      <p className={stylepay.ttl}>Accomodation</p>
                      <p className={stylepay.resultitem}>{item?.acomodation}</p>
                    </div>
                  </Col>
                  <Col md={3} className="mt-4">
                    <div>
                      <p className={stylepay.ttl}>Duration</p>
                      <p className={stylepay.resultitem}>
                        {item?.transpotation}
                      </p>
                      <p className={stylepay.ttl}>Transpotation</p>
                      <p className={stylepay.resultitem}>
                        {item?.transpotation}
                      </p>
                    </div>
                  </Col>
                  <Col md={3} className="mt-4">
                    <div className={stylepay.paymentimg}>
                      <form>
                        <div>
                          <label htmlFor="payment">
                            <img
                              src={pay}
                              alt="logopayment"
                              className={stylepay.imgpay}
                            />
                          </label>
                          <input
                            type="file"
                            id="payment"
                            name="payment"
                            hidden
                          />
                          <p className={stylepay.paymentttl}>
                            Upload payment Proof
                          </p>
                        </div>
                      </form>
                    </div>
                  </Col>
                </Row>
              </Card>
              <button className={stylepay.btnpayment}>Pay</button>
            </Col>
          ))}
        </Row>
      </div>
    </>
  );
}

export default OrderPage;
