import React, { useState, useEffect, useContext } from "react";
import { Row, Container, Col } from "react-bootstrap";
import data from "../../data/Trip.json";
import NavbarComp from "../../components/navbar/NavbarComp";
import "./styledet.css";
import hotlog from "../../assets/icons/hotlog.png";
import transpot from "../../assets/icons/plnlog.png";
import clock from "../../assets/icons/jamlog.png";
import eat from "../../assets/icons/eatlog.png";
import cal from "../../assets/icons/callog.png";
import { useHistory } from "react-router-dom";
import { UserContext } from "../../context/Contextusr";

function DetailPage({ match }) {
  const [state, dispatch] = useContext(UserContext);
  const rp = require("rupiah-format");
  let history = useHistory();
  const datas = JSON.parse(localStorage.getItem("trip"));
  const user = JSON.parse(localStorage.getItem("userlogin"));
  const [detail, setDetail] = useState({});
  const [qty, setqty] = useState(1);

  const { id } = match.params;

  useEffect(() => {
    let idd = id - 1;
    setDetail(datas[idd]);
  }, []);

  const handlePlusqty = () => {
    setqty(qty + 1);
  };

  const handleMinqty = () => {
    setqty(qty - 1);
  };

  const subtotal = detail?.price * qty;
  const handleBook = () => {
    dispatch({
      type: "ADD_TRIP",
      payload: {
        id: detail?.id,
        userorder: user?.fullname,
        title: detail?.title,
        day: detail?.day,
        night: detail?.night,
        country: detail?.country,
        eat: detail?.eat,
        date: detail?.date,
        subtotal: subtotal,
        paymentprof: null,
        detail: detail?.price,
        acomodation: detail?.acomodation,
        transpotation: detail?.transpotation,
        qty: qty,
        status: "waiting payment",
      },
    });
    dispatch({
      type: "SAVE_TRIP",
    });
    history.push("/client/order");
  };

  return (
    <>
      <NavbarComp />

      <div className="content">
        <h2>{detail?.title}</h2>
        <p>{detail?.country}</p>
        <Row>
          <Col md={12}>
            <img
              src={"/assets/" + detail?.img?.[2]}
              alt="dfafdsf"
              width="100%"
              height=""
              className="imgs"
            />
          </Col>
        </Row>
        <Row className="mt-4">
          <Col md={4} className="clg">
            <img
              src={"/assets/" + detail?.img?.[1]}
              alt="dfafdsf"
              width="100%"
              height="167"
            />
          </Col>
          <Col md={4} className="clg">
            <img
              src={"/assets/" + detail?.img?.[0]}
              alt="dfafdsf"
              width="100%"
              height="167"
            />
          </Col>
          <Col md={4} className="clg">
            <img
              src={"/assets/" + detail?.img?.[3]}
              alt="dfafdsf"
              width="100%"
              height="167"
            />
          </Col>
        </Row>
        <p>Information Trip</p>
        <div className="container-icon">
          <div>
            <span>Accomodation</span>
            <br />
            <img src={hotlog} alt="logohotel" />
            <span>Hotel {detail?.title} </span>
          </div>
          <div>
            <span>Transpotation</span>
            <br />
            <img src={transpot} alt="logohotel" />
            <span>{detail?.transpotation}</span>
          </div>
          <div>
            <span>Eat</span>
            <br />
            <img src={eat} alt="logohotel" />
            <span>{detail.eat}</span>
          </div>
          <div>
            <span>Duration</span>
            <br />
            <img src={clock} alt="logohotel" />
            <span>Hotel</span>
          </div>
          <div>
            <span>Date Trip</span>
            <br />
            <img src={cal} alt="logohotel" />
            <span>Hotel</span>
          </div>
        </div>
      </div>
      <br />
      <br />
    </>
  );
}

export default DetailPage;
