import React, { useState, useEffect } from "react";
import styledet from "./detail.module.css";
import { Card, Row, Col, Table } from "react-bootstrap";
import logodewe from "../../assets/icons/Icon2.png";
import bcaa from "../../assets/icons/bca.jpg";
import { API } from "../../config/api";
function Detailorder({ showDetail, id, setshowDetail }) {
  const [detail, setDetail] = useState({});
  const [isLoading, setisloading] = useState(false);
  const ids = id;

  console.log(ids);
  const getDetail = async () => {
    try {
      const response = await API.get("/transactionn/" + ids);
      setDetail(response.data.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    setTimeout(() => {}, 2000);
  }, []);
  console.log(detail);
  const handleClose = () => setshowDetail(false);
  return (
    <>
      {isLoading ? (
        <div>
          <p>loading...</p>
        </div>
      ) : (
        showDetail && (
          <div onClick={handleClose} className={styledet.containt}>
            <Card className={styledet.cardorder}>
              <Row>
                <Col md={6}>
                  <img src={logodewe} alt="logodewe" width="200" />
                </Col>
                <Col md={6}>
                  <div className={styledet.booking}>
                    <h2 className={styledet.textbooking}>Booking</h2>
                    <p className={styledet.dateorder}>
                      Saturday, 27-11-2021 06:20:02
                    </p>
                  </div>
                </Col>
              </Row>
              <br />
              <Row>
                <Col md={3}>
                  <div className={styledet.containtorder}>
                    <p className={styledet.title}>Jalan jalan di Surabaya</p>
                    <p className={styledet.countries}>Australia</p>
                    <p className={styledet.statuspay}>Waiting Approve</p>
                  </div>
                </Col>
                <Col md={3}>
                  <div className={styledet.containtorder}>
                    <p className={styledet.titleorder}>Date Trip</p>
                    <p className={styledet.result}>20 Agustus 2022</p>
                    <p className={styledet.titleorder}>Accomodation</p>
                    <p className={styledet.result}>Hotel Bali</p>
                  </div>
                </Col>
                <Col md={3}>
                  <div className={styledet.containtorder}>
                    <p className={styledet.titleorder}>Duration</p>
                    <p className={styledet.result}>20 Agustus 2022</p>
                    <p className={styledet.titleorder}>Transpotation</p>
                    <p className={styledet.result}>Citi Link</p>
                  </div>
                </Col>
                <Col md={3}>
                  <div className={styledet.containtorder}>
                    <img
                      src={bcaa}
                      alt="payemntimage"
                      width="180"
                      height="180"
                      className={styledet.imagepay}
                    />
                  </div>
                </Col>
              </Row>

              <Table responsive className={styledet.tableorder}>
                <thead>
                  <tr>
                    <th>No</th>
                    <th>Fullname</th>
                    <th>Gender</th>
                    <th>No Phone</th>
                    <th></th>
                    <th></th>
                  </tr>
                </thead>
                <tbody>
                  <td>1</td>
                  <td></td>
                  <td></td>
                  <td></td>
                  <td className={styledet.orderqty}>
                    Qty
                    <br />
                    <br />
                    Total
                  </td>
                  <td className={styledet.orderqty}></td>
                </tbody>
              </Table>
            </Card>
          </div>
        )
      )}
    </>
  );
}

export default Detailorder;
