import React, { useEffect, useState } from "react";
import { io } from "socket.io-client";
import NavbarComp from "../../../components/navbar/NavbarComp";
import stylechat from "./chat.module.css";
import { Row, Col } from "react-bootstrap";
import imgadmin from "../../../assets/icons/admin.png";
let socket;

function ChatPage() {
  const [Contacts, setContacts] = useState({});

  useEffect(() => {
    socket = io("http://localhost:5005", {
      auth: {
        token: localStorage.getItem("tokenkey"),
      },
    });

    loadContact();

    return () => {
      socket.disconnect();
    };
  }, []);

  const loadContact = () => {
    socket.emit("LoadContactAdmin");

    socket.on("admin contact", (data) => {
      const dataContact = {
        ...data,
        message: "Click here to start message",
      };
      setContacts(dataContact);
    });
  };
  console.log(Contacts);

  return (
    <>
      <NavbarComp />
      <div className={stylechat.containerchat}>
        <Row>
          <Col md={4}>
            <div className={stylechat.Contact}>
              <div className={stylechat.chatadmin}>
                <img
                  src={imgadmin}
                  alt="contactadmin"
                  className={stylechat.imgadmin}
                />
                <p className={stylechat.nameadmin}>{Contacts.fullname}</p>
              </div>
              <p>{Contacts.message}</p>
            </div>
          </Col>
          <Col md={8}></Col>
        </Row>
      </div>
    </>
  );
}

export default ChatPage;
