import React, { useState, useEffect } from "react";
import { Container, Table } from "react-bootstrap";
import NavbarComp from "../../../components/navbar/NavbarComp";
import styletb from "./styletbl.module.css";
import { Link } from "react-router-dom";
import ic from "../../../assets/icons/Vector.png";

import { API } from "../../../config/api";
import Detailorder from "../../../components/modals/Detailorder";

function TransactionAdm() {
  const [transactions, settransactios] = useState([]);
  const [modal, setmodal] = useState(null);

  const getTransactions = async () => {
    try {
      const response = await API.get("/transactions");
      console.log(response.data);
      settransactios(response.data.data);
    } catch (error) {}
  };

  useEffect(() => {
    getTransactions();
  }, []);

  const handleShow = () => {
    const modals = (
      <div>
        <h1>Modal</h1>
      </div>
    );

    setmodal(modals);
  };

  return (
    <>
      <NavbarComp />
      <Container>
        <div className={styletb.tb}>
          <h2>Incoming Transaction</h2>
          <Table responsive>
            <thead>
              <tr>
                <th>No</th>
                <th>User</th>
                <th>Trip</th>
                <th>Bukti Transfer</th>
                <th>Status Payment</th>
                <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {transactions.map((item, idx) => (
                <tr key={idx}>
                  <td>{idx + 1}</td>
                  <td>{item?.User?.fullname}</td>
                  <td>{item?.Trips.title}</td>
                  <td>
                    {item?.payment === null ? (
                      <span>-</span>
                    ) : (
                      <img
                        src={
                          "http://localhost:5005/uploads/payment/" +
                          item?.payment
                        }
                        alt="bca.jpg"
                        height="40"
                        width="40"
                      />
                    )}
                  </td>

                  <td>
                    {item?.status !== "Approve" ? (
                      <span className={styletb.pd}>Pending</span>
                    ) : (
                      <span></span>
                    )}
                  </td>

                  <td>
                    <img src={ic} alt="logodetailorder" onClick={handleShow} />
                  </td>
                </tr>
              ))}
            </tbody>
          </Table>
        </div>
      </Container>
      {modal && modal}
    </>
  );
}

export default TransactionAdm;
