import React from "react";
import { Container } from "react-bootstrap";
import "./dropdown.css";
import { Dropdown, Image } from "react-bootstrap";
import { Link, useHistory } from "react-router-dom";
import prof from "../assets/icons/user1.png";

function DropComp(props) {
  const userstatus = JSON.parse(localStorage.getItem("userlogin"));
  const handleClose = () => props.setDropdown(false);
  let history = useHistory();
  const handleTo = () => {
    history.push("/client/profile");
    handleClose();
  };

  return (
    props.show && (
      <>
        <div className="drop" onClick={handleClose} />
        {userstatus.status !== "admin" ? (
          <div className="containerdrop rounded shadow">
            <Dropdown.Item onClick={handleTo}>
              <Image src={prof} />
              <span>Profile</span>
            </Dropdown.Item>
            <Dropdown.Item>
              <Image src={prof} />
              <span>Pay</span>
            </Dropdown.Item>
            <Dropdown.Item>
              <Image src={prof} />
              <span>Logout</span>
            </Dropdown.Item>
          </div>
        ) : (
          <div className="containerdrop rounded shadow">
            <Dropdown.Item onClick={handleTo}>
              <Image src={prof} />
              <span>Trip</span>
            </Dropdown.Item>
            <Dropdown.Item>
              <Image src={prof} />
              <span>Logout</span>
            </Dropdown.Item>
          </div>
        )}
      </>
    )
  );
}

export default DropComp;
