import React, { useEffect, useContext, useState } from "react";
import { Route, Switch, useHistory } from "react-router-dom";
import HomePage from "./pages/home/HomePage";
import DetailPage from "./pages/details/DetailPage";
import Private from "./private/Private";
import PrivateAdmin from "./private/PrivateAdmin";

import { API, AuthToken } from "./config/api";
import { UserContext } from "./context/Contextusr";
import "./App.css";

if (localStorage.tokenkey) {
  AuthToken(localStorage.tokenkey);
}

function App() {
  const [state, dispatch] = useContext(UserContext);
  const [isLoading, setisLoading] = useState(true);
  let history = useHistory();

  const cekAuth = async () => {
    try {
      const response = await API.get("/userauth");
      const user = response?.data?.data;
      dispatch({
        type: "AUTH",
        payload: user,
      });
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    cekAuth();
  }, []);

  useEffect(() => {
    if (!localStorage.tokenkey) {
      history.push("/");
    } else {
      if (state?.user?.role === "admin") {
        history.push("/admin");
      } else if (state?.user?.role === "user") {
        history.push("/");
      }
    }
  }, [state]);

  const Apps = () => {
    return (
      <>
        <Switch>
          <Route path="/" exact component={HomePage} />
          <Route path="/detail/:id" exact component={DetailPage} />
          <Private />
          <PrivateAdmin />
        </Switch>
      </>
    );
  };

  useEffect(() => {
    setTimeout(() => {
      Apps();
      setisLoading(false);
    }, 2000);
  }, []);
  return (
    <>
      {isLoading ? (
        <div>
          <p>Loading...</p>
        </div>
      ) : (
        <Apps />
      )}
    </>
  );
}

export default App;
