import React, { useState, useEffect } from "react";
import { Container, Row, Col } from "react-bootstrap";
import stylepay from "./stylepay.module.css";
import NavbarComp from "../../../components/navbar/NavbarComp";
import logic from "../../../assets/icons/Icon2.png";
import pay from "../../../assets/icons/receipt.png";
function OrderPage() {
  const [order, setOrder] = useState([]);
  const orders = JSON.parse(localStorage.getItem("ordertrip"));
  useEffect(() => {
    setOrder(orders);
  }, []);
  console.log(order);
  return (
    <>
      <NavbarComp />
      <Container>
        {orders?.map((item, idx) => (
          <div className={stylepay.book} key={idx}>
            <Row>
              <Col md={6}>
                <img src={logic} alt="ffsdfsd" className={stylepay.im} />
              </Col>
              <Col md={6}>
                <h2 className={stylepay.lg}>Booking</h2>
                <br />
                <p className={stylepay.ld}>{item?.date}</p>
              </Col>
            </Row>
            <div className={stylepay.tt}>
              <Row>
                <Col md={3} className={stylepay.col}>
                  <p className={stylepay.tp}>6D/4N Fun tessie Vaction</p>
                </Col>
                <Col md={3} className={stylepay.col}>
                  <p>Date Trip</p>
                  <p>{item.da}</p>
                </Col>
                <Col md={3} className={stylepay.col}>
                  <p>Duration</p>
                </Col>
                <Col md={3} className={stylepay.col}>
                  <form>
                    <div
                      className="form-dominant color-dominant mb-2"
                      style={{
                        border: "1px solid #fdcb6e",
                        padding: 3,
                        height: "75px",
                        width: "135px",
                        margin: "auto",
                        borderRadius: 10,
                      }}
                    >
                      <label htmlFor="payment" className=" mt-2">
                        <center>
                          <img
                            src={pay}
                            alt="paymentlogo"
                            height="30"
                            className="ml-5"
                          />
                        </center>
                      </label>
                      <input type="file" name="paymen" id="payment" hidden />
                    </div>
                    <span className={stylepay.ll}>Upload Payment Aprove</span>
                  </form>
                </Col>
              </Row>
            </div>
          </div>
        ))}
      </Container>
    </>
  );
}

export default OrderPage;
