import React, { useState, useEffect, useContext } from "react";
import { Row, Container, Col } from "react-bootstrap";
import data from "../../data/Trip.json";
import NavbarComp from "../../components/navbar/NavbarComp";
import "./styledet.css";
import res from "../../assets/icons/resort.png";
import air from "../../assets/icons/airplane.png";
import eat from "../../assets/icons/eating.png";
import calend from "../../assets/icons/calendar.png";
import { useHistory } from "react-router-dom";
import { UserContext } from "../../context/Contextusr";

function DetailPage({ match }) {
  const [state, dispatch] = useContext(UserContext);
  let history = useHistory();
  const datas = JSON.parse(localStorage.getItem("trip"));
  const user = JSON.parse(localStorage.getItem("userlogin"));
  const [detail, setDetail] = useState({});
  const [qty, setqty] = useState(1);
  const [subtotal, setsubtotal] = useState(0);

  const { id } = match.params;

  useEffect(() => {
    let idd = id - 1;
    setDetail(datas[idd]);
    setsubtotal(detail?.price);
  }, []);

  const handlePlusqty = () => {
    setqty(qty + 1);
  };

  const handleMinqty = () => {
    setqty(qty - 1);
  };
  console.log(detail?.price);
  const handleBook = () => {
    dispatch({
      type: "ADD_TRIP",
      payload: {
        id: detail?.id,
        userorder: user?.fullname,
        title: detail?.title,
        day: detail.day,
        night: detail.night,
        country: detail?.country,
        eat: detail?.eat,
        datebooking: new Date().getTime().toString(),
        date: detail?.date,
        paymentprof: "",
        detail: detail?.price,
        acomodation: detail?.acomodation,
        transpotation: detail?.transpotation,
        qty: qty,
        status: "waiting payment",
      },
    });

    dispatch({
      type: "SAVE_TRIP",
    });
    history.push("/client/order");
  };

  return (
    <>
      <NavbarComp />
      <br />
      <br />
      <div className="titl">
        <h2 className="text-center">
          {detail.day}D/{detail?.night}N {detail.title}
        </h2>
        <p className="ctry">{detail?.country}</p>
      </div>

      <div className="imgs">
        <Row>
          <Col md={12} className="clg mb-3">
            <center>
              <img
                src={"/assets/" + detail?.img?.[2]}
                alt="dfafdsf"
                className="bg1"
              />
            </center>
          </Col>
        </Row>
        {/* =============================== */}
        <Row>
          <Col md={3} className="clg">
            <img
              src={"/assets/" + detail?.img?.[1]}
              alt="dfafdsf"
              width="380"
              height="150"
            />
          </Col>
          <Col md={3} className="clg">
            <img
              src={"/assets/" + detail?.img?.[0]}
              alt="dfafdsf"
              width="380"
              height="150"
            />
          </Col>
          <Col md={3} className="clg">
            <img
              src={"/assets/" + detail?.img?.[3]}
              alt="dfafdsf"
              width="380"
              height="150"
            />
          </Col>
        </Row>
      </div>
      <br />
      <div className="cnt">
        <h5 className="in">Information Trip</h5>
        <Row className="infor">
          <Col md={3} className="column">
            <span>Acomodation</span>
            <br />
            <p className="pt">
              <img src={res} alt="dafada" height="29" className="im" />
              Hotel {detail.night} Night
            </p>
          </Col>

          <Col md={2} className="column">
            <span>Transpotasion</span>
            <br />
            <p className="pt">
              <img src={air} alt="dafada" height="29" className="im" />
              {detail.transpotation}
            </p>
          </Col>
          <Col md={2} className="column">
            <span>Eat</span>
            <br />
            <p className="pt">
              <img src={eat} alt="dafada" height="29" className="im" />
              {detail.eat}
            </p>
          </Col>
          <Col md={2} className="column">
            <span>Duration</span>
            <br />
            <p className="pt">
              <img src={res} alt="dafada" height="29" className="im" />
              {detail.day}Day/{detail.night}Night
            </p>
          </Col>
          <Col md={2} className="column">
            <span>Date Trip</span>
            <br />
            <p className="pt">
              <img src={calend} alt="dafada" height="29" className="im" />
              {detail.date}
            </p>
          </Col>
        </Row>
        <div className="">
          <p className="jd">Description</p>
          <p className="dsc">{detail.description}</p>
        </div>

        <Row className="btnp">
          <Col md={6} className="cl">
            <p className="prsn sn">
              <strong>IDR. {detail.price} / Person </strong>
            </p>
          </Col>
          <Col md={6} className="ml-auto cl">
            <div className="ds">
              <button className="btq mr-3" onClick={handlePlusqty}>
                +
              </button>
              <p className="qty">{qty}</p>
              <button className="btq ml-3" onClick={handleMinqty}>
                -
              </button>
            </div>
          </Col>
        </Row>

        <hr className="hr" />
        <Row className="btnp">
          <Col md={6} className="cl">
