import React, { useState, useEffect } from "react";
import { Row, Col, Card } from "react-bootstrap";
import "./card.css";
import { Link } from "react-router-dom";
import { API } from "../../config/api";
function CardTrip() {
  const datas = JSON.parse(localStorage.getItem("trip"));
  const [Trips, setTrips] = useState([]);
  const getTrips = async () => {
    try {
      const response = await API.get("/trips");
      setTrips(response.data.data);
    } catch (error) {
      console.log(error);
    }
  };
  console.log(Trips);
  useEffect(() => {
    getTrips();
  }, []);

  const images = Trips?.map((datas, i) => {
    return JSON.parse(datas?.imagestrip);
  });

  return (
    <>
      <Row>
        {Trips?.map((item, idx) => (
          <Col key={idx} md={4}>
            <Card className="crdd" key={idx}>
              <Link to={"/detail/" + item?.id}>
                <center>
                  <img
                    src={
                      "http://localhost:5005/uploads/images/" + images[idx][1]
                    }
                    alt={images[1][idx]}
                    className="imgl"
                  />
                </center>
              </Link>
              <h5>
                {item?.day}D/{item?.night}N {item?.title}
              </h5>
              <p className="to">IDR. {item?.price}</p>
              <p className="cnty ml-auto">{item?.country}</p>
            </Card>
          </Col>
        ))}
      </Row>
    </>
  );
}

export default CardTrip;
