import { createContext, useReducer } from "react";
export const UserContext = createContext();

const initialState = {
  stsLogin: false,
  user: {},
  order: [],
};

const reducer = (state, action) => {
  const { type, payload } = action;

  switch (type) {
    case "ADD_TRIP":
      return {
        ...state.order,
        order: [...state.order, payload],
      };

    case "PAYMENT":
      const order = JSON.parse(localStorage.getItem("userorder"));
      const datas = state?.order?.filter((itm, idx) => idx !== payload.id);

      let newData = {
        id: order[payload.id]?.id,
        userorder: order[payload.id].userorder,
        title: order[payload.id]?.title,
        day: order[payload.id]?.day,
        night: order[payload.id]?.night,
        country: order[payload.id]?.country,
        eat: order[payload.id]?.eat,
        datebooking: new Date().getTime().toString(),
        date: order[payload.id]?.date,
        detail: order[payload.id]?.price,
        paymentprof: order[payload.id]?.image,
        acomodation: state?.[payload.id]?.acomodation,
        transpotation: order[payload.id]?.transpotation,
        qty: order[payload.id]?.qty,
        status: "waiting approve",
      };
      return {
        ...state.order,
        order: [...datas, newData],
      };

    case "SAVE_TRIP":
      localStorage.setItem("ordertrip", JSON.stringify(state.order));
      return state;
    case "USER_SUCCESS":
    case "LOGIN_SUCCESS":
      localStorage.setItem("userlogin", JSON.stringify(payload));
      return {
        ...state,
        stsLogin: true,
        user: payload,
      };

    case "AUTH_ERROR":
    case "LOGOUT":
      localStorage.removeItem("userlogin");
      return {
        stsLogin: false,
        user: {},
        order: [...state.order],
      };
    default:
      throw new Error();
  }
};

export const ContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <UserContext.Provider value={[state, dispatch]}>
      {children}
    </UserContext.Provider>
  );
};
