import React, { useState, useContext } from "react";
import { Modal, Form } from "react-bootstrap";
import "./modals.css";
import icn from "../../assets/icons/l2.png";
import icns from "../../assets/icons/l1.png";
import swal from "sweetalert";
import { UserContext } from "../../context/Contextusr";
import { useHistory } from "react-router-dom";

function LoginMod({ handleClose, handleShow }) {
  let history = useHistory();
  const users = JSON.parse(localStorage.getItem("users"));
  const [state, dispatch] = useContext(UserContext);

  const [form, setForm] = useState({
    email: "",
    password: "",
  });

  const handleChange = (e) => {
    setForm({
      ...form,
      [e.target.name]: e.target.value,
    });
  };

  const handleLogin = (e) => {
    e.preventDefault();

    let data = users.find((x) => x?.email === form?.email);
    if (data?.password === form?.password) {
      if (data?.status === "admin") {
        history.push("/admin");
        swal("Login Successfully");
        dispatch({
          type: "LOGIN_SUCCESS",
          payload: data[0],
        });
      } else if (data?.status === "user") {
        history.push("/");
        swal("Login Successfully");
        dispatch({
          type: "LOGIN_SUCCESS",
          payload: data[0],
        });
      }
    } else {
      console.log("login incorrect!!!");
    }
  };

  return (
    <>
      <Modal show={handleShow} onHide={handleClose} className="mm">
        <div className="tt">
          <img src={icn} alt="dfdsf" height="100" width="120" />
          <img
            src={icns}
            alt="dfdsf"
            height="70"
            width="90"
            className="ml-auto"
          />
        </div>

        <p className="text-center loo">Sign in</p>

        <Modal.Body className="mdl">
          <Form className="formm" onSubmit={handleLogin}>
            <Form.Group controlId="formBasicEmail">
              <Form.Label>Email address</Form.Label>
              <Form.Control
                type="email"
                className="inpt"
                onChange={handleChange}
                name="email"
              />
            </Form.Group>

            <Form.Group controlId="formBasicPassword">
              <Form.Label>Password</Form.Label>
              <Form.Control
                type="password"
                className="inpt"
                onChange={handleChange}
                name="password"
              />
            </Form.Group>
            <button className="modbtn" type="submit">
              Login
            </button>
            <p className="text-center here">
              Dont have an Account? Klik{" "}
              <span>
                <strong>Here </strong>{" "}
              </span>{" "}
            </p>
          </Form>
        </Modal.Body>
      </Modal>
    </>
  );
}

export default LoginMod;
