import React, { useEffect, useState } from "react";
import { io } from "socket.io-client";
import NavbarComp from "../../../components/navbar/NavbarComp";
import stylechat from "./chat.module.css";
import { Row, Col } from "react-bootstrap";
import imgadmin from "../../../assets/icons/admin.png";
let socket;

function ChatPage() {
  const [Contacts, setContacts] = useState({});

  useEffect(() => {
    socket = io("http://localhost:5005", {
      auth: {
        token: localStorage.getItem("tokenkey"),
      },
    });

    loadContact();

    return () => {
      socket.disconnect();
    };
  }, []);

  const loadContact = () => {
    socket.emit("LoadAllContact");

    socket.on("user contact", (data) => {
      setContacts(data);
    });
  };
  console.log(Contacts);

  return (
    <>
      <NavbarComp />
      <div className={stylechat.containerchat}>
        <Row>
          <Col md={4}>
            {Contacts?.map((item, idx) => (
              <div className={stylechat.Contact} key={idx}>
                <Row>
                  <Col md={2}>
                    <img
                      src={imgadmin}
                      alt="contactadmin"
                      className={stylechat.imgadmin}
                    />
                  </Col>
                  <Col md={6}>
                    <p className={stylechat.nameadmin}>{item.fullname}</p>
                    <p className={stylechat.message}>Click start to message</p>
                  </Col>
                </Row>
              </div>
            ))}
          </Col>
          <Col md={8}>
            <div className={stylechat.chatadmin}></div>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default ChatPage;
