import React, { useState, useEffect, useContext } from "react";
import { Container, Row, Col, Table } from "react-bootstrap";
import stylepay from "./stylepay.module.css";
import NavbarComp from "../../../components/navbar/NavbarComp";
import logic from "../../../assets/icons/Icon2.png";
import pay from "../../../assets/icons/receipt.png";
import { Link } from "react-router-dom";
import swal from "sweetalert";
import { UserContext } from "../../../context/Contextusr";

function OrderPage() {
  const orders = JSON.parse(localStorage.getItem("ordertrip"));
  const [state, dispatch] = useContext(UserContext);
  const [order, setOrder] = useState([]);
  const [dataorder, setDataorder] = useState([]);
  const [form, setForm] = useState({
    payment: "",
  });
  const [previmage, setprevimage] = useState(null);
  const user = JSON.parse(localStorage.getItem("userlogin"));
  const data = orders?.filter((x) => x?.userorder === user?.fullname);
  useEffect(() => {
    setOrder(data);
  }, []);

  const handleChange = (e, idx) => {
    setForm({
      ...form,
      [e.target.name]:
        e.target.type === "file" ? e.target.files : e.target.value,
    });
    if (e.target.name === "payment") {
      let url = URL.createObjectURL(e.target.files[0]);
      setprevimage(url);
    }
  };

  const handlePayment = (id) => {
    let newData = {
      id: order[id]?.id,
      userorder: order[id]?.userorder,
      title: order[id]?.title,
      day: order[id]?.day,
      night: order[id]?.night,
      country: order[id]?.country,
      eat: order[id]?.eat,
      datebooking: new Date().getTime().toString(),
      date: order[id]?.date,
      detail: order[id]?.price,
      paymentprof: previmage,
      acomodation: order[id]?.acomodation,
      transpotation: order[id]?.transpotation,
      qty: order[id]?.qty,
      status: "waiting approve",
    };

    console.log(state.order);
    swal("Payment Success");
    // localStorage.setItem("ordertrip", JSON.stringify(dataorder));
  };

  return (
    <>
      <NavbarComp />
      <Container>
        {!order?.length ? (
          <>
            <div className={stylepay.dd}>
              <h1 className={stylepay.qw}>Not Found</h1>
            </div>
          </>
        ) : (
          order?.map((item, idx) => (
            <div key={idx}>
              <div className={stylepay.book}>
                <Row>
                  <Col md={6}>
                    <img src={logic} alt="ffsdfsd" className={stylepay.im} />
                  </Col>
                  <Col md={6}>
                    <h2 className={stylepay.lg}>Booking</h2>
                    <br />
                    <p className={stylepay.ld}>{item?.newDate}</p>
                  </Col>
                </Row>
                <div className={stylepay.tt}>
                  <Row>
                    <Col md={3} className={stylepay.col}>
                      <p className={stylepay.tp}>{item?.title}</p>
                      <p className={stylepay.ds}>{item?.country}</p>
                      <p className={stylepay.stss}>{item?.status}</p>
                    </Col>
                    <Col md={3} className={stylepay.col}>
                      <p>Date Trip</p>
                      <p className={stylepay.ds}>{item?.date}</p>
                      <p>Accomodation</p>
                      <p className={stylepay.ds}>
                        {item?.acomodation}
                        {item?.night} Night
                      </p>
                    </Col>
                    <Col md={3} className={stylepay.col}>
                      <p>Duration</p>
                      <p className={stylepay.ds}>
                        {item?.day}Day/{item?.night}Night
                      </p>
                      <p>Transpotation</p>
                      <p className={stylepay.ds}>{item?.transpotation}</p>
                    </Col>
                    <Col md={3} className={stylepay.col}>
                      <form>
                        {previmage ? (
                          <>
                            <center>
                              <img
                                src={previmage}
                                alt="logosfsf"
                                height="80"
                                width="80"
                              />
                            </center>
                            <br />
                          </>
                        ) : (
                          <div
                            className="form-dominant color-dominant mb-2"
                            style={{
                              border: "1px solid #fdcb6e",
                              padding: 3,
                              height: "75px",
                              width: "135px",
                              margin: "auto",
                              borderRadius: 10,
                            }}
                          >
                            <label htmlFor="payment" className=" mt-2">
                              <center>
                                <img
                                  src={pay}
                                  alt="paymentlogo"
                                  height="30"
                                  className="ml-5"
                                />
                              </center>
                            </label>

                            <input
                              type="file"
                              name="payment"
                              id="payment"
                              hidden
                              onChange={handleChange}
                            />
                          </div>
                        )}

                        <span className={stylepay.ll}>
                          Upload Payment Aprove
                        </span>
                      </form>
                    </Col>
                  </Row>
                </div>
                <Table>
                  <thead>
                    <tr>
                      <th>No</th>
                      <th>fullname</th>
                      <th>Gender</th>
                      <th>Phone</th>
                      <th></th>
                      <th></th>
                    </tr>
                  </thead>
                  <tbody>
                    <tr>
                      <td>{idx + 1}</td>
                      <td>{user?.fullname}</td>
                      <td>{user?.gender}</td>
                      <td>{user?.nophone}</td>
                      <td>
                        Qty
                        <br />
                        <br />
                        Total
                      </td>
                      <td>
                        : {item?.qty}
                        <br />
                        <br />
                        {item?.detail}
                      </td>
                    </tr>
                    <tr></tr>
                  </tbody>
                </Table>
              </div>
              {item.status === "waiting approve" ? (
                <span></span>
              ) : (
                <button
                  className={stylepay.pay}
                  onClick={() => handlePayment(idx)}
                >
                  Pay
                </button>
              )}
            </div>
          ))
        )}
      </Container>
    </>
  );
}

export default OrderPage;
