import React, { useState, useEffect } from "react";
import NavbarComp from "../../../components/navbar/NavbarComp";
import stylechat from "./chat.module.css";
import { Row, Col } from "react-bootstrap";
import { io } from "socket.io-client";

let socket;
function UserChat() {
  const [Message, setMessage] = useState("");
  const [messages, setmessages] = useState([]);

  useEffect(() => {
    socket = io("http://localhost:5005");
    socket.on("message", (message) => {
      setmessages([...messages, message]);
    });
    return () => {
      socket.disconnect();
    };
  }, []);

  const handleMessage = (e) => {
    setMessage(e.target.value);
  };

  const handleSend = () => {
    socket.emit("ListenSendMessage", Message);
    // setmessages([...messages, Message]);
    setMessage("");
  };

  return (
    <>
      <NavbarComp />

      <div className={stylechat.container}>
        <Row>
          <Col md={2}></Col>
          <Col md={9}>
            <div className={stylechat.containermessage}>
              <br />
              <br />
              {messages?.map((item, idx) => (
                <p key={idx} className={stylechat.chat}>
                  {item}
                </p>
              ))}
            </div>
            <input
              type="text"
              name="message"
              className={stylechat.inputmessage}
              onChange={handleMessage}
            />
            <button onClick={handleSend} className={stylechat.btnchat}>
              Send
            </button>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default UserChat;
