import React, { useState, useEffect } from "react";
import { Modal } from "react-bootstrap";
import upld from "../../assets/icons/upld.png";
function EditPhoto(props) {
  const [prev, setprev] = useState(null);
  const [form, setform] = useState({
    imageProfile: "",
  });

  const handleChange = (e) => {
    setform({
      [e.target.name]:
        e.target.type === "file" ? e.target.files : e.target.value,
    });
    if (e.target.name === "imageProfile") {
      setprev(URL.createObjectURL(e.target.file));
    }
  };

  return (
    <>
      <Modal show={props.handleShow} onHide={props.handleClose}>
        <Modal.Body>
          <div>
            <form>
              <p>Create Your Photo Profile</p>
              <label htmlFor="imageProfile">
                <img src={upld} alt="imageprofile" />
              </label>
              <input
                name="imageProfile"
                type="file"
                onChange={handleChange}
                id="imageProfile"
                hidden
              />
              <br />
              <button>Edit</button>
            </form>
          </div>
        </Modal.Body>
      </Modal>
    </>
  );
}

export default EditPhoto;
