import React from "react";
import "./stylealert.css";
function AlertPayment(props) {
  const handleClose = () => props.hidemodal(false);
  return (
    props.showmodal && (
      <div
        className="containeralert"
        hide={props.hidemodal}
        onClick={handleClose}
      >
        <div className="contentpayment">
          <p className="textpay">
            Your payment will be confirmed within 1 x 24 hours To see orders
            click
            <b> Here </b> thank you
          </p>
        </div>
      </div>
    )
  );
}

export default AlertPayment;
