import {Row,Container,Col} from 'react-bootstrap'
import data from '../../data/Trip.json'
import NavbarComp from '../../components/navbar/NavbarComp'
import './styledet.css'
function DetailPage({match}) {
    const [detail,setDetail] = useState({})
    const id = match.params.id

    useEffect( () => {
        setDetail(data[id])
    },[detail] )

console.log(detail);
    return (
        <>
        <NavbarComp/>
        <br/>
        <br/>
        <div className="titl">
        <h2 className="text-center">{detail.title}</h2>
       <p className="ctry">{detail?.country}</p>
        </div>

        <div>
            {detail?.img?.map( (items,idx) => 
           <img src={"/assets/"+items[0]} alt="dfafdsf" width="450" height="200" />

            )}
        </div>
        </>
    )
}

export default DetailPage
