import { createContext, useReducer } from "react";
export const UserContext = createContext();

const initialState = {
  stsLogin: false,
  user: {},
  order: [],
};

const reducer = (state, action) => {
  const { type, payload, id } = action;

  switch (type) {
    case "ADD_TRIP":
      return {
        ...state.order,
        order: [...state.order, payload],
      };

    case "PAYMENT":
      const order = JSON.parse(localStorage.getItem("userorder"));
      const datas = order?.filter((itm, idx) => idx !== id);

      let newData = {
        id: order[id]?.id,
        userorder: order[id].userorder,
        title: order[id]?.title,
        day: order[id]?.day,
        night: order[id]?.night,
        country: order[id]?.country,
        eat: order[id]?.eat,
        datebooking: new Date().getTime().toString(),
        date: order[id]?.date,
        detail: order[id]?.price,
        paymentprof: null,
        acomodation: state?.[id]?.acomodation,
        transpotation: order[id]?.transpotation,
        qty: order[id]?.qty,
        status: "waiting approve",
      };
      return {
        ...state.order,
        order: [...datas, newData],
      };

    case "SAVE_TRIP":
      localStorage.setItem("ordertrip", JSON.stringify(state.order));
      return state;
    case "USER_SUCCESS":
    case "LOGIN_SUCCESS":
      localStorage.setItem("userlogin", JSON.stringify(payload));
      return {
        ...state,
        stsLogin: true,
        user: payload,
      };

    case "AUTH_ERROR":
    case "LOGOUT":
      localStorage.removeItem("userlogin");
      return {
        stsLogin: false,
        user: {},
        order: [...state.order],
      };
    default:
      throw new Error();
  }
};

export const ContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <UserContext.Provider value={[state, dispatch]}>
      {children}
    </UserContext.Provider>
  );
};
