import React, { useState, useEffect } from "react";
import { Container, Row, Form, Col } from "react-bootstrap";
import NavbarComp from "../../../components/navbar/NavbarComp";
import uploadsimage from "../../../assets/icons/upld.png";
import styletrip from "./addtrip.module.css";
import { API } from "../../../config/api";

function AddnewTrip() {
  const [form, setform] = useState({
    title: "",
    idcountry: "",
    accomodation: "",
    transpotation: "",
    eat: "",
    day: "",
    night: "",
    datetrip: "",
    price: "",
    quota: "",
    desc: "",
  });
  const [SelectedFIle, setSelectedFile] = useState([]);
  const [images, setimages] = useState([]);
  const [country, setcountry] = useState([]);

  const getCountry = async () => {
    try {
      const response = await API.get("/countries");

      setcountry(response.data.data);
    } catch (error) {
      console.log(error);
    }
  };

  useEffect(() => {
    getCountry();
  }, []);

  const handleChange = (e) => {
    setform({
      ...form,
      [e.target.name]:
        e.target.type === "file" ? e.target.files : e.target.value,
    });

    // if (e.target.name === "imagestrip") {
    //   setimages((img) => img.concat(e.target.files));
    //   const formarr = Array.from(e.target.files).map((file) =>
    //     URL.createObjectURL(file)
    //   );
    //   setSelectedFile((previmage) => previmage.concat(formarr));
    //   Array.from(e.target.files).map((file) => URL.revokeObjectURL(file));
    // }
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    const body = JSON.stringify(form);
    console.log(body);
  };
  console.log(form);
  return (
    <>
      <NavbarComp />
      <br />
      <br />

      <div className={styletrip.containertrip}>
        <Form encType="multipart/form-data" onSubmit={handleSubmit}>
          <Form.Group className="mb-3">
            <Form.Label>Title Trip</Form.Label>
            <Form.Control
              type="text"
              name="title"
              placeholder="ex: Adventure in.."
              onChange={handleChange}
            />
          </Form.Group>

          <Form.Group>
            <Form.Label>Country</Form.Label>
            <Form.Control as="select" name="idcountry">
              <option>--</option>
              {country.map((item, idx) => (
                <option value={item?.id} key={idx}>
                  {item?.namecountry}
                </option>
              ))}
            </Form.Control>
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Accomodation</Form.Label>
            <Form.Control
              type="text"
              name="accomodation"
              placeholder="Enter email"
              onChange={handleChange}
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Transpotation</Form.Label>
            <Form.Control
              type="text"
              name="tranpotation"
              placeholder="Enter email"
              onChange={handleChange}
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Eat</Form.Label>
            <Form.Control type="text" name="eat" onChange={handleChange} />
          </Form.Group>
          <Form.Label>Duration</Form.Label>
          <Row>
            <Col md={4}>
              <Form.Group className="mb-3">
                <Form.Label>Day</Form.Label>
                <Form.Control
                  type="number"
                  onChange={handleChange}
                  name="day"
                  placeholder=""
                />
              </Form.Group>
            </Col>
            <Col md={4}>
              <Form.Group className="mb-3">
                <Form.Label>Night</Form.Label>
                <Form.Control
                  type="number"
                  name="night"
                  placeholder=""
                  onChange={handleChange}
                />
              </Form.Group>
            </Col>
          </Row>

          <Form.Group className="mb-3">
            <Form.Label>Date Trip</Form.Label>
            <Form.Control
              type="date"
              name="datetrip"
              placeholder="Enter email"
              onChange={handleChange}
            />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label>Price</Form.Label>
            <Form.Control type="number" name="price" onChange={handleChange} />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>quota</Form.Label>
            <Form.Control type="number" name="quota" onChange={handleChange} />
          </Form.Group>
          <Form.Group className="mb-3">
            <Form.Label>Description</Form.Label>
            <Form.Control type="text" name="desc" onChange={handleChange} />
          </Form.Group>

          <Form.Group className="mb-3">
            <Form.Label htmlFor="imagestrip" className={styletrip.imgs}>
              {!SelectedFIle.length ? (
                <>
                  <span className={styletrip.attache}>Attache here</span>
                  <img
                    src={uploadsimage}
                    alt="logoimageuploads"
                    className={styletrip.imgp}
                  />
                </>
              ) : (
                SelectedFIle.map((itemfile) => (
                  <img
                    src={itemfile}
                    alt="imagefile"
                    height="66"
                    width="106"
                    className={styletrip.imagefile}
                  />
                ))
              )}
            </Form.Label>
            <Form.Control
              type="file"
              id="imagestrip"
              hidden
              name="imagestrip"
              onChange={handleChange}
              multiple
            />
          </Form.Group>
          <button className={styletrip.btntrip}>Add Trip</button>
        </Form>
      </div>
      <br />
    </>
  );
}

export default AddnewTrip;
