import React, { useState, useEffect } from "react";
import NavbarComp from "../../../components/navbar/NavbarComp";
import { io } from "socket.io-client";
import stylechat from "./chat.module.css";
import { Row, Col } from "react-bootstrap";
let socket;

function UserChat() {
  const [msg, setmsg] = useState("");
  const [Message, setMessage] = useState("");

  useEffect(() => {
    const socket = io("http://localhost:5005");

    socket.on("user", (item) => {
      console.log(item);
    });

    return () => {
      socket.disconnect();
    };
  }, []);

  const handleSend = () => {
    socket.emit("user", "hello guys");
  };

  return (
    <>
      <NavbarComp />

      <div className={stylechat.container}>
        <Row>
          <Col md={2}></Col>
          <Col md={9}>
            <div className={stylechat.containermessage}></div>

            <input
              type="text"
              name="message"
              className={stylechat.inputmessage}
            />

            <button onClick={handleSend} className={stylechat.btnchat}>
              Send
            </button>
          </Col>
        </Row>
      </div>
    </>
  );
}

export default UserChat;
