  //       type: "AUTH",
  //       payload: user,
  //     });
  //   } catch (error) {
  //     console.log(error);
  //   }
  // };

  // useEffect(() => {
  //   cekAuth();
  // }, []);

  const Apps = () => {
    return (
      <>
        <Switch>
          <Route path="/" exact component={HomePage} />
          <Route path="/detail/:id" exact component={DetailPage} />
          <Private />
          <PrivateAdmin />
        </Switch>
      </>
    );
  };

  useEffect(() => {
    setTimeout(() => {
      Apps();
      setisLoading(false);
    }, 2000);
  }, []);
  return (
    <>
      {isLoading ? (
        <div>
          <p>Loading...</p>
        </div>
      ) : (
        <Apps />
      )}
    </>
  );
}

export default App;
