import { createContext, useReducer } from "react";
export const UserContext = createContext();

const initialState = {
  stsLogin: false,
  user: {},
  order: [],
};

const reducer = (state, action) => {
  const { type, payload } = action;

  switch (type) {
    case "ADD_TRIP":
      return {
        ...state.order,
        order: [...state.order, payload],
      };

    case "PAYMENT":
      // const order = JSON.parse(localStorage.getItem("userorder"));
      const userlog = JSON.parse(localStorage.getItem("userlogin"));
      const data = JSON.parse(localStorage.getItem("ordertrip"));
      const datas = data?.filter((itm, idx) => idx !== userlog?.id);
      localStorage.setItem("ordertrip", JSON.stringify(payload));

      return {
        ...state,
        order: [payload],
      };

    case "SAVE_TRIP":
      localStorage.setItem("ordertrip", JSON.stringify(state?.order));
      return state;
    case "USER_SUCCESS":
    case "LOGIN_SUCCESS":
      localStorage.setItem("userlogin", JSON.stringify(payload));
      return {
        ...state,
        stsLogin: true,
        user: payload,
      };

    case "AUTH_ERROR":
    case "LOGOUT":
      localStorage.removeItem("userlogin");
      return {
        stsLogin: false,
        user: {},
        order: [...state?.order],
      };
    default:
      throw new Error();
  }
};

export const ContextProvider = ({ children }) => {
  const [state, dispatch] = useReducer(reducer, initialState);

  return (
    <UserContext.Provider value={[state, dispatch]}>
      {children}
    </UserContext.Provider>
  );
};
